mapper.insert:
INSERT INTO 
   BSNS_SCHEDULE (
         BSNS_CODE
         , SCHEDULE_CD
         , SCHEDULE_DT
         , ST_HOUR
         , END_HOUR
         , SUBJECT
         , CONTENT
         , REG_ID
         , REG_DT
         , MOD_ID
         , MOD_DT
   ) VALUES (
         
         #{bsns_code}
         , #{scheduleCd}
         , #{scheduleDt}
         , #{stHour}
         , #{endHour}
         , #{subject}
         , #{content}
         , #{session_id}
         , now()
         , #{session_id}
         , now()
   )

mapper.update:
UPDATE	BSNS_SCHEDULE
SET		SCHEDULE_DT = #{scheduleDt}
		, ST_HOUR = #{stHour}
		, END_HOUR = #{endHour}
		, SUBJECT = #{subject}
		, CONTENT = #{content}
		, MOD_DT = now()
		, MOD_ID = #{session_id}
WHERE	SCHEDULE_SN = #{scheduleSn}
