mapper.select:
SELECT BMB.BSNS_CODE ,BMB.BOARD_SN ,BMB.BOARD_GBN_CD ,BMB.SUBJECT ,BMB.CONTENTS ,BMB.HIT_COUNT ,BMB.LIKE_COUNT ,BMBF.BOARD_FL_SN ,BMBF.ORIGIN_FL_NM ,BMBF.FL_NM ,BMBF.FL_PATH FROM BSNS_MNG_BOARD BMB LEFT OUTER JOIN BSNS_MNG_BOARD_FL BMBF ON BMB.BSNS_CODE = BMBF.BSNS_CODE AND BMB.BOARD_SN = BMBF.BOARD_SN AND BMBF.USE_YN = 'Y' WHERE 1=1 AND BMB.USE_YN = 'Y' AND BMB.BSNS_CODE = #{bsns_code} AND BMB.BOARD_GBN_CD = #{board_gbn_cd}
AND BMB.SUBJECT LIKE CONCAT('%',#{searchWord},'%')
AND BMB.CONTENTS LIKE CONCAT('%',#{searchWord},'%')
AND CONCAT(BMB.SUBJECT,BMB.CONTENTS) LIKE CONCAT('%',#{searchWord},'%')
ORDER BY BMB.BOARD_SN DESC LIMIT #{limitFirst}, #{limitSecond}

mapper.update:
UPDATE BSNS_MNG_BOARD
   SET SUBJECT = #{subject}
      ,CONTENTS = #{editor}
      ,MOD_DT = now()
      ,MOD_ID = 'aa'
 WHERE 1=1
   AND BSNS_CODE = #{bsns_code}
   AND BOARD_SN  = #{board_Sn}

mapper.select:
SELECT BMB.BSNS_CODE ,BMB.BOARD_SN ,BMB.BOARD_GBN_CD ,BMB.SUBJECT ,BMB.CONTENTS ,BMB.HIT_COUNT ,BMB.LIKE_COUNT ,BMBF.BOARD_FL_SN ,BMBF.ORIGIN_FL_NM ,BMBF.FL_NM ,BMBF.FL_PATH FROM BSNS_MNG_BOARD BMB LEFT OUTER JOIN BSNS_MNG_BOARD_FL BMBF ON BMB.BSNS_CODE = BMBF.BSNS_CODE AND BMB.BOARD_SN = BMBF.BOARD_SN AND BMBF.USE_YN = 'Y' WHERE 1=1 AND BMB.USE_YN = 'Y' AND BMB.BSNS_CODE = #{bsnsCode} AND BMB.BOARD_GBN_CD = #{boardGbnCd}
AND BMB.SUBJECT LIKE CONCAT('%',#{searchWord},'%')
AND BMB.CONTENTS LIKE CONCAT('%',#{searchWord},'%')
AND CONCAT(BMB.SUBJECT,BMB.CONTENTS) LIKE CONCAT('%',#{searchWord},'%')
ORDER BY BMB.BOARD_SN DESC LIMIT #{limitFirst}, #{limitSecond}

mapper.update:
UPDATE BSNS_MNG_BOARD
   SET SUBJECT = #{subject}
      ,CONTENTS = #{editor}
      ,MOD_DT = now()
      ,MOD_ID = 'aa'
 WHERE 1=1
   AND BSNS_CODE = #{bsnsCode}
   AND BOARD_SN  = #{boardSn}

UPDATE BSNS_MNG_BOARD_FL
   SET ORIGIN_FL_NM = #{originFlNm}
      ,FL_NM = #{flNm}
      ,FL_PATH = #{flPath}
      ,MOD_DT = now()
      ,MOD_ID = 'aa'
 WHERE 1=1 
   AND BSNS_CODE = #{bsnsCode}
   AND BOARD_SN  = #{boardSn}
   AND BOARD_FL_SN = #{boardFlSn}
