mapper.update:
UPDATE BSNS_BOARD AA 					/* 업체_게시판 */
	   INNER JOIN BSNS_BOARD_BOOK AB	/* 업체_게시판_책소개 */
	    ON AA.BSNS_CODE = AB.BSNS_CODE
	   AND AA.BOARD_SN  = AB.BOARD_SN
   SET AA.SUBJECT 		= #{subject}
      ,AA.CONTENT 		= #{editor}
      ,AB.BOOK_WRITER 	= #{bookWriter}
      ,AB.BOOK_PUBLISHER= #{bookPublisher}
      ,AB.BOOK_PRICE	= #{bookPrice}
      ,AA.MOD_ID 		= 'aa'
      ,AA.MOD_DT 		= now()
      ,AB.MOD_ID 		= 'aa'
      ,AB.MOD_DT 		= now()
 WHERE 1=1
   AND AA.BSNS_CODE = #{bsnsCode}
   AND AA.BOARD_SN  = #{boardSn}

mapper.insert:
INSERT INTO BSNS_BOARD_LIKE(
	 BSNS_CODE
	,BOARD_SN
	,MEM_ID
	,LIKE_YN
	,REG_ID
	,REG_DT
	,MOD_ID
	,MOD_DT
)VALUES(
	 #{bsnsCode}
	,#{boardSn}
	,#{sessionId}
	,#{saveLike}
	,#{sessionId}
	,now()
	,#{sessionId}
	,now()
)
ON DUPLICATE KEY 
UPDATE 	LIKE_YN = #{saveLike},
		MOD_ID 	= #{sessionId},
		MOD_DT	= now()

mapper.select:
SELECT AA.BSNS_CODE /* 업체_코드 */ ,AA.BOARD_SN /* 게시판_일련번호 */ ,AA.BOARD_GBN_CD /* 게시판_구분_코드 */ ,(SELECT PSCC.DETAIL_CODE_NM FROM PKHL_SYS_CMMN_CODE PSCC WHERE 1=1 AND PSCC.USE_YN = 'Y' AND PSCC.BSNS_CODE = AA.BSNS_CODE AND PSCC.CMMN_CODE = AA.BOARD_GBN_CD ) AS BOARD_GBN_CD_NM /* 게시판_구분_코드_명 */ ,AA.SUBJECT /* 제목 */ ,AA.CONTENT /* 내용 */ ,AA.HIT_COUNT /* 조회수 */ ,AB.BOARD_FL_SN /* 게시판_파일_일련번호 */ ,AB.ORIGIN_FL_NM /* 파일_원본_명 */ ,AB.FL_NM /* 파일_명 */ ,AB.FL_PATH /* 파일_경로 */ FROM BSNS_BOARD AA /* 업체_게시판 */ LEFT OUTER JOIN BSNS_BOARD_FL AB /* 업체_게시판_파일 */ ON AA.BSNS_CODE = AB.BSNS_CODE AND AA.BOARD_SN = AB.BOARD_SN AND AB.USE_YN = 'Y' WHERE 1=1 AND AA.USE_YN = 'Y' AND AA.BSNS_CODE = #{bsnsCode} AND AA.BOARD_GBN_CD = #{boardGbnCd}
AND AA.SUBJECT LIKE CONCAT('%',#{searchWord},'%')
AND AA.CONTENT LIKE CONCAT('%',#{searchWord},'%')
AND CONCAT(AA.SUBJECT,AA.CONTENT) LIKE CONCAT('%',#{searchWord},'%')
ORDER BY AA.BOARD_SN DESC LIMIT #{limitFirst}, #{limitSecond}

mapper.update:
UPDATE BSNS_BOARD
   SET SUBJECT = #{subject}
      ,CONTENT = #{editor}
      ,MOD_DT = now()
      ,MOD_ID = 'aa'
 WHERE 1=1
   AND BSNS_CODE = #{bsnsCode}
   AND BOARD_SN  = #{boardSn}

UPDATE BSNS_BOARD_FL
   SET ORIGIN_FL_NM = #{originFlNm}
      ,FL_NM = #{flNm}
      ,FL_PATH = #{flPath}
      ,MOD_DT = now()
      ,MOD_ID = 'aa'
 WHERE 1=1 
   AND BSNS_CODE = #{bsnsCode}
   AND BOARD_SN  = #{boardSn}
   AND BOARD_FL_SN = #{boardFlSn}

UPDATE BSNS_BOARD_IMG
   SET ORIGIN_FL_NM = #{originFlNm}
      ,FL_NM = #{flNm}
      ,FL_PATH = #{flPath}
      ,MOD_DT = now()
      ,MOD_ID = 'aa'
 WHERE 1=1 
   AND BSNS_CODE = #{bsnsCode}
   AND BOARD_SN  = #{boardSn}
   AND BOARD_FL_SN = #{boardFlSn}
