mapper.select:
SELECT MEM_PW
      ,USE_YN
FROM BSNS_MEMBER
WHERE 1=1
  AND MEM_ID = #{memId}
  AND BSNS_CODE = #{bsnsCode}

SELECT MEM_ID
 	  ,MEM_NM
 	  ,MEM_GBN_CD
FROM BSNS_MEMBER
WHERE 1=1
  AND USE_YN = 'Y'
  AND MEM_ID = #{memId}
  AND MEM_PW = #{memPw}
  AND BSNS_CODE = #{bsnsCode}

mapper.insert:
    	INSERT INTO BSNS_MEMBER(
    		  BSNS_CODE
    		, MEM_ID
    		, MEM_PW
    		, MEM_NM
    		, MEM_EMAIL
    		, MEM_GBN_CD
    		, MEM_PHONE
    		, REG_DT
    		, REG_ID
    		
    		, USE_YN
			
    	)VALUES(
    		  #{bsnsCode}
    		, #{memId}
    		, #{memPw}
    		, #{memNm}
    		, #{memEmail}
    		, #{memGbnCd}
    		, #{memPhone}
    		, now()
    		, #{memId}
    		
    		, 'S'
			
    	)

mapper.update:
    	
    	UPDATE BSNS_MEMBER
    	SET   MEM_PW	= #{memPw}
    		, MEM_NM	= #{memNm}
    		, MEM_EMAIL	= #{memEmail}
    		, MEM_GBN_CD= #{memGbnCd}
    		, MEM_PHONE	= #{memPhone}
    		, MOD_DT	= now()
    		, MOD_ID	= #{sessionId}
    	WHERE 1=1
    	  AND BSNS_CODE = #{bsnsCode}
    	  AND MEM_ID    = #{memId}
    	  AND MEM_SN    = #{memSn}

UPDATE BSNS_MEMBER
SET USE_YN = 'N'
WHERE 1=1
  AND BSNS_CODE = #{bsnsCode}
  AND MEM_ID    = #{memId}
  AND MEM_SN    = #{memSn}

mapper.select:
		SELECT COUNT(*) AS CNT
		FROM BSNS_MEMBER
		WHERE 1=1
    	  AND MEM_ID = #{memId}
		  AND BSNS_CODE = #{bsnsCode}
