mapper.select:
SELECT MEM_PW
      ,USE_YN
FROM BSNS_MEMBER
WHERE 1=1
  AND MEM_ID = #{mem_id}
  AND BSNS_CODE = #{bsns_code}

SELECT MEM_ID
 	  ,MEM_NM
 	  ,MEM_GBN_CD
FROM BSNS_MEMBER
WHERE 1=1
  AND USE_YN = 'Y'
  AND MEM_ID = #{mem_id}
  AND MEM_PW = #{mem_pw}
  AND BSNS_CODE = #{bsns_code}

mapper.insert:
    	INSERT INTO BSNS_MEMBER(
    		  BSNS_CODE
    		, MEM_ID
    		, MEM_PW
    		, MEM_NM
    		, MEM_EMAIL
    		, MEM_GBN_CD
    		, MEM_PHONE
    		, REG_DT
    		, REG_ID
    		
    		, USE_YN
			
    	)VALUES(
    		  #{bsns_code}
    		, #{mem_id}
    		, #{mem_pw}
    		, #{mem_nm}
    		, #{mem_email}
    		, #{mem_gbn_cd}
    		, #{mem_phone}
    		, now()
    		, #{mem_id}
    		
    		, 'S'
			
    	)

mapper.update:
    	
    	UPDATE BSNS_MEMBER
    	SET   MEM_PW	= #{mem_pw}
    		, MEM_NM	= #{mem_nm}
    		, MEM_EMAIL	= #{mem_email}
    		, MEM_GBN_CD= #{mem_gbn_cd}
    		, MEM_PHONE	= #{mem_phone}
    		, MOD_DT	= now()
    		, MOD_ID	= #{session_id}
    	WHERE 1=1
    	  AND BSNS_CODE = #{bsns_code}
    	  AND MEM_ID    = #{mem_id}
    	  AND MEM_SN    = #{mem_sn}

UPDATE BSNS_MEMBER
SET USE_YN = 'N'
WHERE 1=1
  AND BSNS_CODE = #{bsns_code}
  AND MEM_ID    = #{mem_id}
  AND MEM_SN    = #{mem_sn}

mapper.select:
		SELECT COUNT(*) AS CNT
		FROM BSNS_MEMBER
		WHERE 1=1
    	  AND MEM_ID = #{mem_id}
		  AND BSNS_CODE = #{bsns_code}
