mapper.select:
SELECT BSNS_CODE , MEM_SN , MEM_ID , MEM_NM , MEM_EMAIL , MEM_NICKNM , MEM_GBN_CD ,(SELECT DETAIL_CODE_NM FROM PKHL_SYS_CMMN_CODE WHERE 1=1 AND USE_YN = 'Y' AND CMMN_CODE = MEM_GBN_CD AND BSNS_CODE = #{bsns_code} ) AS MEM_GBN_CD_NM , MEM_PHONE , USE_YN , (CASE WHEN USE_YN = 'Y' THEN '활성화' WHEN USE_YN = 'N' THEN '비활성화' WHEN USE_YN = 'S' THEN '권한부여 대기중' END ) AS REMARK_NM FROM BSNS_MEMBER WHERE 1=1 AND BSNS_CODE = #{bsns_code} AND MEM_GBN_CD NOT IN ('G01999')
AND MEM_ID LIKE CONCAT('%',#{searchWord},'%')
AND MEM_NM LIKE CONCAT('%',#{searchWord},'%')
AND MEM_NICKNM LIKE CONCAT('%',#{searchWord},'%')
AND CONCAT(MEM_NM, MEM_NICKNM) LIKE CONCAT('%',#{searchWord},'%')
AND MEM_EMAIL LIKE CONCAT('%',#{searchWord},'%')
AND MEM_PHONE LIKE CONCAT('%',#{searchWord},'%')
AND MEM_GBN_CD = #{searchAuthor}
AND USE_YN = #{searchRemark}
LIMIT #{limitFirst}, #{limitSecond}

mapper.update:
UPDATE BSNS_MEMBER
SET USE_YN = #{saveremark}
   ,MOD_ID = #{session_id}
   ,MOD_DT = now()
WHERE 1=1
  AND BSNS_CODE = #{bsns_code}
  AND MEM_SN = #{mem_sn}
