mapper.select:
SELECT AA.BSNS_CODE /* 업체_코드 */ ,AA.BOARD_SN /* 게시판_일련번호*/ ,AA.BOARD_GBN_CD /* 게시판_구분_코드 */ ,AA.SUBJECT /* 제목 */ ,AA.CONTENT /* 내용 */ ,AA.HIT_COUNT /* 조회 수 */ ,AA.LIKE_COUNT /* 좋아요 수 */ FROM BSNS_BOARD AA /* 업체_게시판 */ WHERE 1=1 AND AA.USE_YN = 'Y' AND AA.BSNS_CODE = #{bsnsCode} AND AA.BOARD_GBN_CD = #{boardGbnCd}
AND AA.SUBJECT LIKE CONCAT('%',#{searchWord},'%')
AND AA.CONTENT LIKE CONCAT('%',#{searchWord},'%')
AND CONCAT(AA.SUBJECT, AA.CONTENT) LIKE CONCAT('%',#{searchWord},'%')
ORDER BY AA.BOARD_SN DESC LIMIT #{limitFirst}, #{limitSecond}

mapper.update:
UPDATE BSNS_BOARD AA 					/* 업체_게시판 */
   SET AA.SUBJECT 		= #{subject}
      ,AA.CONTENT 		= #{editor}
      ,AA.MOD_ID 		= #{sessionId}
      ,AA.MOD_DT 		= now()
 WHERE 1=1
   AND AA.BSNS_CODE = #{bsnsCode}
   AND AA.BOARD_SN  = #{boardSn}

UPDATE BSNS_BOARD
   SET USE_YN = 'N'
	  ,MOD_ID = #{sessionId}
      ,MOD_DT = now()
 WHERE 1=1
   AND BSNS_CODE = #{bsnsCode}
   AND BOARD_SN  = #{boardSn}
