mapper.select:
SELECT COUNT(AA.BOARD_SN) FROM BSNS_BOARD AA WHERE 1=1 AND AA.USE_YN ='Y' AND AA.BSNS_CODE = #{bsnsCode} AND SUBSTR(AA.BOARD_GBN_CD,1,3) = #{boardClGbnCd}
AND AA.BOARD_GBN_CD = #{selectCategory}
AND AA.SUBJECT LIKE CONCAT('%',#{searchWord},'%')
AND AA.REG_ID IN (SELECT BM.MEM_ID FROM BSNS_MEMBER BM WHERE 1=1 AND BM.USE_YN = 'Y' AND BM.BSNS_CODE = AA.BSNS_CODE AND BM.MEM_NM LIKE CONCAT('%',#{searchWord},'%'))

SELECT AA.BSNS_CODE /* 업체_코드 */ ,AA.BOARD_SN /* 게시판_일련번호*/ ,AA.BOARD_GBN_CD /* 게시판_구분_코드 */ ,(SELECT PSCC.DETAIL_CODE_NM FROM PKHL_SYS_CMMN_CODE PSCC WHERE 1=1 AND PSCC.USE_YN = 'Y' AND PSCC.BSNS_CODE = AA.BSNS_CODE AND PSCC.CMMN_CODE = AA.BOARD_GBN_CD ) AS BOARD_GBN_CD_NM /* 게시판_구분_코드_명 */ ,AA.SUBJECT /* 제목 */ ,AA.HIT_COUNT /* 조회 수 */ ,AA.REG_ID /* 등록_ID */ ,(SELECT BM.MEM_NM FROM BSNS_MEMBER BM WHERE 1=1 AND BM.USE_YN = 'Y' AND BM.BSNS_CODE = AA.BSNS_CODE AND BM.MEM_ID = AA.REG_ID) AS MEM_NM /* 회원_명 */ ,DATE_FORMAT(AA.REG_DT,'%Y-%m-%d') AS REG_DT /* 등록_일시 */ FROM BSNS_BOARD AA WHERE 1=1 AND AA.USE_YN ='Y' AND AA.BSNS_CODE = #{bsnsCode} AND SUBSTR(AA.BOARD_GBN_CD,1,3) = #{boardClGbnCd}
AND AA.BOARD_GBN_CD = #{selectCategory}
AND AA.SUBJECT LIKE CONCAT('%',#{searchWord},'%')
AND AA.REG_ID IN (SELECT BM.MEM_ID FROM BSNS_MEMBER BM WHERE 1=1 AND BM.USE_YN = 'Y' AND BM.BSNS_CODE = AA.BSNS_CODE AND BM.MEM_NM LIKE CONCAT('%',#{searchWord},'%'))
ORDER BY AA.REG_DT DESC LIMIT #{limitFirst}, #{limitSecond}

mapper.update:
UPDATE BSNS_BOARD
   SET SUBJECT 		= #{subject}
      ,CONTENT 		= #{editor}
      ,BOARD_GBN_CD = #{teacherGbnCd}
      ,MOD_DT 		= now()
      ,MOD_ID 		= #{sessionId}
 WHERE 1=1
   AND BSNS_CODE = #{bsnsCode}
   AND BOARD_SN  = #{boardSn}

UPDATE BSNS_BOARD
   SET USE_YN = 'N'
      ,MOD_DT = now()
      ,MOD_ID = #{sessionId}
 WHERE 1=1
   AND BSNS_CODE = #{bsnsCode}
   AND BOARD_SN  = #{boardSn}

mapper.select:
SELECT 1 AS RE_LEVEL ,AA.BSNS_CODE ,AA.BOARD_SN ,AA.BOARD_RE_SN ,AA.BOARD_UP_RE_SN ,AA.CONTENT ,AA.MEM_ID ,(SELECT BM.MEM_NM FROM BSNS_MEMBER BM WHERE 1=1 AND BM.USE_YN = 'Y' AND BM.BSNS_CODE = AA.BSNS_CODE AND BM.MEM_ID = AA.REG_ID) AS MEM_NM ,AA.BOARD_RE_SN AS ORDR FROM BSNS_BOARD_RE AA WHERE 1=1 AND NOT EXISTS (SELECT 1 FROM BSNS_BOARD_RE WHERE BOARD_RE_SN = AA.BOARD_UP_RE_SN) AND AA.USE_YN = 'Y' AND AA.BSNS_CODE = #{bsnsCode} AND AA.BOARD_SN = #{boardSn}
UNION ALL
SELECT 2 AS RE_LEVEL ,AB.BSNS_CODE ,AB.BOARD_SN ,AB.BOARD_RE_SN ,AB.BOARD_UP_RE_SN ,AB.CONTENT ,AB.MEM_ID ,(SELECT BM.MEM_NM FROM BSNS_MEMBER BM WHERE 1=1 AND BM.USE_YN = 'Y' AND BM.BSNS_CODE = AB.BSNS_CODE AND BM.MEM_ID = AB.REG_ID) AS MEM_NM ,AA.BOARD_RE_SN AS ORDR FROM BSNS_BOARD_RE AA ,BSNS_BOARD_RE AB WHERE 1=1 AND NOT EXISTS (SELECT 1 FROM BSNS_BOARD_RE WHERE BOARD_RE_SN = AA.BOARD_UP_RE_SN) AND AA.BOARD_RE_SN = AB.BOARD_UP_RE_SN AND AA.USE_YN = 'Y' AND AB.USE_YN = 'Y' AND AA.BSNS_CODE = #{bsnsCode} AND AA.BOARD_SN = #{boardSn}
ORDER BY ORDR, BOARD_RE_SN;

mapper.update:
UPDATE BSNS_BOARD_RE
SET CONTENT = #{content}
	,MOD_ID = #{sessionId}
	,MOD_DT = now()
WHERE 1=1
  AND USE_YN = 'Y'
  AND BSNS_CODE = #{bsnsCode}
  AND BOARD_SN  = #{boardSn}
  AND BOARD_RE_SN = #{boardReSn}

UPDATE BSNS_BOARD_RE
SET USE_YN = 'N'
	,MOD_ID = #{sessionId}
	,MOD_DT = now()
WHERE 1=1
  AND USE_YN = 'Y'
  AND BSNS_CODE = #{bsnsCode}
  AND BOARD_SN  = #{boardSn}
  AND BOARD_RE_SN = #{boardReSn}
